import { Component } from 'react';

class App extends Component {


  constructor(props){
    super(props);
    this.state={

      items: [],
      txtContent :""
    }
  }
  txtChange=(e)=>{
    this.setState({txtContent:e.target.value})
  }
  addItem=(e)=>{
    let currentText=this.state.txtContent
    let currentItems=this.state.items
    currentItems.push(currentText)
    this.setState({items:currentItems})
  }
  remove=(i)=>{
    let currentItems=this.state.items
    currentItems.splice(i,1)
    this.setState({items:currentItems})
  }
  render(){
    return (
      <div className="App">
        <input type="text" onChange={this.txtChange}/>
        <button onClick={this.addItem}>Add</button>
        <ul>
          {this.state.items.map((itm,k)=>{
            return(
              <li>{itm} <button onClick={()=>{this.remove(k)}}>Delete</button></li>
            )
          })}
          <script src="https://cdn.jsdelivr.net/npm/@popperjs/core@2.11.6/dist/umd/popper.min.js" integrity="sha384-oBqDVmMz9ATKxIep9tiCxS/Z9fNfEXiDAYTujMAeBAsjFuCZSmKbSSUnQlmh/jp3" crossorigin="anonymous"></script>
<script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0-alpha1/dist/js/bootstrap.min.js" integrity="sha384-mQ93GR66B00ZXjt0YO5KlohRA5SY2XofN4zfuZxLkoj1gXtW8ANNCe9d5Y3eG5eD" crossorigin="anonymous"></script>
        </ul>

      </div>
    );

  }

}

export default App;
