import 'bootstrap/dist/css/bootstrap.css';
import { Component } from 'react';

class App extends Component {


  constructor(props) {
    super(props);
    this.state = {

      items: [],
      txtContent: ""
    }
  }
  txtChange = (e) => {
    this.setState({ txtContent: e.target.value })
  }
  addItem = (e) => {
    let currentText = this.state.txtContent
    let currentItems = this.state.items
    currentItems.push(currentText)
    this.setState({ items: currentItems })
  }
  remove = (i) => {
    let currentItems = this.state.items
    currentItems.splice(i, 1)
    this.setState({ items: currentItems })
  }
  render() {
    return (
      <div className="App">
        <nav className="navbar bg-info">
          <div className="container-fluid">
            <span className="navbar-brand mb-0 h1">To Do List</span>
          </div>
        </nav>
        <div className='p-5'>
          <input type="text" className='m-5' onChange={this.txtChange} />
          <button className='btn btn-primary' onClick={this.addItem}>Add</button>
          <ul>
            {this.state.items.map((itm, k) => {
              return (
                <li>{itm} <button onClick={() => { this.remove(k) }}>Delete</button></li>
              )
            })}

          </ul>

        </div>

      </div>
    );

  }

}

export default App;
